export const addToCart=(items , quantity , varient)=>dispatch=>{
    var cartItem = {
    name : items.name ,
    _id : items._id,
    image : items.image ,
    varient : varient ,
    quantity : quantity ,
    prices : items.prices ,
    price : items.prices[0][varient]* quantity
    
}
dispatch({type:'ADD_TO_CART', payload : cartItem})

localStorage.setItem()
}
