import React ,{useState} from 'react'
import {Modal} from 'react-bootstrap'
import { useDispatch , useSelector } from 'react-redux'
import { addToCart } from '../actions/cartActions'
export default function Menu({ items }) {
  const[ quantity, setquantity]=useState(1)
  const[ varient, setvarient]=useState(items.varients[0])
  const[show,setShow]=useState(false);

const handleClose=()=>setShow(false);
const handleShow=()=>setShow(true);

const dispatch=useDispatch()
function addtocart() {
  const cartItem = {
    name: items.name,
    _id: items._id,
    image: items.image,
    varient: varient,
    quantity: Number(quantity),
    prices: items.prices,
    price: items.prices[0][varient] * quantity,
  };

  const alreadyExists = useSelector((state) =>
    state.cartReducer.cartItems.find(
      (item) => item._id === cartItem._id && item.varient === varient
    )
  );

  if (alreadyExists) {
    dispatch({
      type: 'UPDATE_CART_QUANTITY',
      payload: { _id: cartItem._id, quantity: cartItem.quantity, varient: varient },
    });
  } else {
    dispatch(addToCart(items, quantity, varient));
  }

  handleClose();
}

function addtocart()
{
  dispatch(addToCart(items ,quantity , varient));
}
  return (
    <div className='shadow-lg p-3 mb-5 bg-white rounded'>
      <div onClick={handleShow}> 
      <h1>{items.name}</h1>
      <img src={items.image} alt = "img" className="img-fluid" style={{ height: '200px', width: '200px' }} />
      </div>
      <div className="flex-container">


        <div className='w-100 m-1'>
          <p>Varients</p>
          <select className='form-control'value={varient} onChange={(e)=>{setvarient(e.target.value)}}>
          {items.varients.map((varient,idx)=>{
            return <option value={varient} key={idx}>{varient}</option>
          })}
          </select>
        </div>


        <div className='w-100 m-1'>
          <p>Quantity</p>
          <select className='form-control' value={quantity} onChange={(e)=>{setquantity(e.target.value)}}>
           {[...Array(10).keys()].map((x ,i)=>{

            return <option value={i+1}>{i+1}</option>
            
           })}
          </select>
        </div>
      </div>
      <div className="flex-container">
        <div className='m-1 w-100'>
           <h1 className='mt-1'>Price: {items.prices[0][varient]*quantity} Rs/-</h1>
        </div>
        <div className='m-1 w-100'>
           <button className="btn" onClick={addtocart}>ADD TO CART</button>
        </div>
      </div>

      <Modal show={show} onHide={handleClose}>
        <Modal.Header closeButton>
          <Modal.Title>{items.name}</Modal.Title>
        </Modal.Header>

        <Modal.Body>
          <img src={items.image} alt="img" style={{height:'250px',width:'250px'}}></img> 
          <p>{items.description}</p>
        </Modal.Body>

        <Modal.Footer>
        <button className="btn"  onClick={handleClose}>CLOSE</button>
        </Modal.Footer>

      </Modal>
    </div>
  )
}
