import React, { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { loginUser } from '../actions/userAction';
import { useHistory } from 'react-router-dom'; // Import useHistory

export default function Loginscreen() {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const dispatch = useDispatch();
    const userLogin = useSelector((state) => state.loginUserReducer);
    const { loading, error, userInfo } = userLogin;

    const history = useHistory(); // Initialize useHistory

    useEffect(() => {
        if (userInfo) {
            // User is already logged in, redirect to the homepage
            history.push('/');
        }
    }, [userInfo, history]); // Include history in dependencies

    const login = () => {
        const user = {
            email,
            password,
        };
        console.log("Logging in with user:", user);
        dispatch(loginUser(user));
    };

    return (
        <div>
            <h1>Login screen</h1>
            <input
                required
                type="text"
                placeholder="Email"
                className="form-control"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
            />
            <input
                required
                type="password"
                placeholder="Password"
                className="form-control"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
            />
            <button onClick={login} className="btn mt-3" disabled={loading}>
                {loading ? 'Logging in...' : 'Login'}
            </button>
            {error && <p style={{ color: 'red' }}>{error.message}</p>}
        </div>
    );
}
