export const getAllFoodsReducer=(state={} , action)=>{
    switch(action.type)
    {
        case 'GET_MENU_REQUEST' : return{
            loading : true,
            ...state
        }
        case 'GET_MENU_SUCCESS' : return{
            loading : true,
            foods : action.payload
        }
        case 'GET_MENU_FAILED' : return{
            error : action.payload
        }
        default : return state
    }
}
