import React from 'react'
import {useSelector, useDispatch} from 'react-redux'

import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faMinus } from '@fortawesome/free-solid-svg-icons';
import { faTrash } from '@fortawesome/free-solid-svg-icons';
import { faPlus } from '@fortawesome/free-solid-svg-icons';


export default function Cartscreen() {

    const cartstate =useSelector(state=>state.cartReducer)
    const cartItems = cartstate.cartItems

  return (
    <div>
      <div className="row justify-content-center">
        <div className="col-md-6">
            <h2 style={{fontSize:"45px"}}>My Cart</h2>

                {cartItems.map(item=>{
                    return(<div className='flex-container'>
                    <div className='text-left m-1'>
                        <h1>{item.name}({item.varient})</h1>
                        <h1>Price : {item.quantity}*{item.prices[0][item.varient]}={item.price} </h1>
                        <h1 style={{display:'inline'}}>Quantity : </h1>
                        <FontAwesomeIcon icon={faPlus} style={{ fontSize: '24px', color: 'blue' }} />
                        <b>{item.quantity}</b>
                        <FontAwesomeIcon icon={fa} style={{ fontSize: '24px', color: 'blue' }} />
                        <hr/>

                        </div>
                    <div><img src={item.image} style={{height:'80px' , height:'80px'}}></img></div>
                    <div>
                    <FontAwesomeIcon icon={faTrash} style={{ fontSize: '24px', color: 'red' }} />
                    </div>
                </div>)
                })}


            

        </div>
        <div className="col-md-4"></div>
      </div>
    </div>
  )
}
