export const addToCart=(m , quantity , varient)=>dispatch=>{
    var cartItem = {
    name : menu.name ,
    _id : menu._id,
    image : menu.image ,
    varient : varient ,
    quantity : quantity ,
    prices : menu.prices ,
    price : menu.prices[0][varient]* quantity
    
}
dispatch({type:'ADD_TO_CART', payload : cartItem})
}
