export const getAllFoodsReducer=(state={foods :[]} , action)=>{
    switch(action.type)
    {
        case 'GET_MENU_REQUEST' : return{
            loading : true,
            ...state
        }
        case 'GET_MENU_SUCCESS' : return{
            loading : false,
            foods : action.payload
        }
        case 'GET_MENU_FAILED' : return{
            error : action.payload,
            loading : false,
        }
        default : return state
    }
}
